import Tone from "tone";
import Instrument from './Instrument.class.js';

class Song {
  constructor() {
    this.tempo = 120;
    this.instruments = [];
    this.sequences = [];
  }

  addSynth = (id, type = 'triangle') => {
    this.instruments.push(new Instrument(id, type));
    return this.getSynths();
  };

  getSynths = () => {
    return this.instruments;
  };

  getSynth = idx => {
    return this.instruments[idx];
  };

  updateSynth = (idx, props) => {
    //Envelope update
    this.instruments[idx].updateInstrument()
  };

  playSynth = (idx, notes = []) => {
    this.instruments[idx].activateInstrument(notes);
  };

  play = () => {
    Tone.Transport.start();
  };

  stop = () => {
    Tone.Transport.stop();
  };
}

export default Song;
