import React, { Component } from "react";

export default class PianoRollComponent extends Component {
  componentDidMount() {
    const initialScale = 1;
    (toScale = 6), (rows = 12), (cols = 16);
    for (let scale = toScale; scale > initialScale - 1; scale--) {
      for (let i = 0; i < rows; i++) {
        let step = true;
        let row = document.createElement("tr");
        let noteRow = document.createElement("th");
        noteRow.className = "note-cell";
        noteRow.innerHTML = notes[i] + scale;
        row.appendChild(noteRow);

        for (let j = 0; j < cols; j++) {
          let col = document.createElement("td");
          if (j % 4 === 0) step = !step;
          if (step) col.className = "cell-bar";
          col.setAttribute("note", notes[i]);
          col.setAttribute("scale", scale);
          col.setAttribute("step", j);
          col.addEventListener("click", activateCell);
          row.appendChild(col);
        }
        document.querySelector('#sequencer').appendChild(row);
      }
    }
  }

  render() {
    return <div id="sequencer"></div>;
  }
}
