import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class SynthComponent extends Component {
  oscTypes = ["triangle", "sine", "saw", "square"];

  state = {
    id: null,
    oscillator: null,
    envelope: {
      attack: 0.005,
      decay: 0.1,
      sustain: 0.3,
      release: 1
    }
  };

  componentDidMount() {
    this.setState({
      ...this.state,
      id: this.props.idx,
      oscillator: this.props.oscillator,
      envelope: this.props.envelope,
      active: false
    });
  }

  updateEnvelope(envKey, newValue) {
    const newEnvelope = { ...this.state }.envelope;
    newEnvelope[envKey] = newValue;
    this.setState(
      {
        ...this.state,
        envelope: newEnvelope
      },
      () => {
        this.updateSynth();
      }
    );
  }

  updateOscillator(newValue) {
    const newOscillator = { ...this.state }.oscillator;
    console.log(newOscillator.type, newValue)
    newOscillator.type = newValue;
    this.setState(
      {
        ...this.state,
        oscillator: newOscillator
      },
      () => {
        this.updateSynth();
      }
    );
  }

  updateSynth() {
    this.props.updateSynth(this.state.id, {
      envelope: this.state.envelope,
      oscillator: this.state.oscillator
    });
  }

  playNotes(note) {
    this.props.playSynth(this.state.id, note);
    this.setState({
      ...this.state,
      active: !this.state.active
    });
  }

  render() {
    //Envelope controllers
    const { attack, decay, sustain, release } = this.state.envelope;
    const envelopeControls = { attack, decay, sustain, release };
    return (
      <div className="synth">
        <div className="oscillator-controls">
          <select
            id={`osc-select-${this.state.id}`}
            onChange={e => this.updateOscillator(e)}
          >
            {this.oscTypes.map(oscType => (
              <option key={oscType} value={oscType}>
                {oscType.toUpperCase()}
              </option>
            ))}
          </select>
        </div>
        <div className="envelope-controls">
          {Object.entries(envelopeControls).map(value => {
            return (
              <CircularInput
                key={value[0]}
                radius={35}
                value={value[1]}
                onChange={e => this.updateEnvelope(value[0], e)}
              >
                <CircularTrack strokeWidth={3} />
                <CircularProgress strokeWidth={5} />
                <CircularThumb r={4} />
              </CircularInput>
            );
          })}
          <button
            onClick={() =>
              this.playNotes([
                "E4",
                ["E4", "G4", "B4"],
                "F4",
                ["F4", "A4", "C5"]
              ])
            }
          >
            {this.state.active ? "Stop" : "Play"}
          </button>
        </div>
      </div>
    );
  }
}
