import React from "react";
import "./App.css";
import Song from "./Song.class.js";
import SynthComponent from "./SynthComponent";

export default class App extends React.Component {
  state = {
    song: new Song()
  };

  addNewSynth() {
    const newSong = { ...this.state.song };
    newSong.instruments = this.state.song.addSynth(newSong.instruments.length);
    this.setState({
      ...this.state,
      song: newSong
    });
  }

  updateSynth(idx, props) {
    const newSong = { ...this.state.song };
    newSong.instruments = this.state.song.updateSynth(idx, props);
    this.setState({
      ...this.state,
      song: newSong
    });
  }

  playSynth(idx, notes) {
    this.state.song.playSynth(idx, notes);
  }

  play() {
    this.state.song.play();
  }

  stop() {
    this.state.song.stop();
  }

  render() {
    const synths = this.state.song.getSynths();
    return (
      <div className="App">
        <button onClick={() => this.addNewSynth()}>Add Synth</button>
        <button onClick={() => this.play()}>Play</button>
        <button onClick={() => this.stop()}>Stop</button>
        <div className="synth-rack">
          {synths.map((synth, idx) => {
            console.log('oscillator', synth);
            return (
            <SynthComponent
              key={idx}
              idx={idx}
              {...synth.synth}
              updateSynth={(idx, props) => this.updateSynth(idx, props)}
              playSynth={(idx, notes) => this.playSynth(idx, notes)}
            />
          )})}
        </div>
      </div>
    );
  }
}
