import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class SynthComponent extends Component {
  state = {
    id: null,
    oscillator: {
      type: 'triangle'
    },
    envelope: {
      attack: 0.005,
      decay: 0.1,
      sustain: 0.3,
      release: 1
    }
  };

  componentDidMount() {
      this.setState({
          ...this.state,
          id: this.props.idx,
          oscillator: this.props.oscillator,
          envelope: this.props.envelope
      })
  }

  updateEnvelope(envKey, newValue) {
      const newEnvelope = {...this.state.envelope};
      newEnvelope[envKey] = newValue;
      this.setState({
            ...this.state,
            envelope: newEnvelope
      });
      this.props.updateSynth(this.state.id, {envelope: {...this.state.envelope}, oscillator: {...this.state.oscillator}})
  }

  playNotes(note) {
      this.props.playSynth(this.state.id, note);
  }

  render() {
    //Envelope controllers
    const {attack, decay, sustain, release} = this.state.envelope;
    const envelopeControls = {attack, decay, sustain, release};
    return (
      <div className="envelope-controls">
        {Object.entries(envelopeControls).map((value) => {
          return (
            <CircularInput
              key={value[0]}
              radius={25}
              value={value[1]}
              onChange={(e) => this.updateEnvelope(value[0], e)}
            >
              <CircularTrack strokeWidth={1}/>
              <CircularProgress strokeWidth={3}/>
              <CircularThumb r={2}/>
            </CircularInput>
          );
        })}
        <button onClick={()=> this.playNotes(['E4',['E4', 'G4', 'B4'], 'E4'])}></button>
      </div>
    );
  }
}
