import Tone from "tone";
import Instrument from './Instrument.class.js';

class Song {
  constructor() {
    this.tempo = 120;
    this.instruments = [];
    this.sequences = [];
  }

  addSynth = (id, type = 'triangle') => {
    this.instruments.push(new Instrument(id, type));
    return this.getSynths();
  };

  getSynths = () => {
    return this.instruments;
  };

  getSynth = idx => {
    return this.instruments[idx];
  };

  updateSynth = (idx, props) => {
    this.instruments[idx].updateInstrument(props)
  };

  updateSynthSequence = (idx, notes) => {
    this.instruments[idx].updateSequence(notes);
  }

  activateSynth = (idx) => {
    this.instruments[idx].activateInstrument();
  };

  play = () => {
    Tone.Transport.start();
  };

  stop = () => {
    Tone.Transport.stop();
  };
}

export default Song;
