import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class Synth extends Component {
  render() {
    const oscillator = this.props.oscillator;
    const {attack, decay, sustain, release} = this.props.envelope;
    const envelopeControls = {attack, decay, sustain,release};
    return <div class="envelope-controls">
        {
            Object.entries(envelopeControls).map((key, value) => {
               return <CircularInput value={value} onChange={(key, value) => this.updateEnvelope(key, value)}>
                <CircularTrack />
                <CircularProgress />
                <CircularThumb />
                </CircularInput>
            })
        }
    </div>;
  }
}
