import Tone from 'tone';

class Song {
    constructor() {
        this.tempo = 120;
        this.instruments = [];
    }

    addSynth = () => {
        this.instruments.push(new Tone.Synth().toMaster());
        return this.getSynths();
    }

    getSynths = () => {
        return this.instruments;
    }

    getSynth = (idx) => {
        return this.instruments[idx];
    }

    updateSynth = (idx, props) => {
        console.log(props);

        const synthSettings = {
            oscillator: new Tone.Oscillator(props.oscillator.frequency.value, props.oscillator._type),
            envelope: new Tone.AmplitudeEnvelope(props.envelope)
        }
        console.log(synthSettings)
        //this.instruments[idx] = new Tone.Synth(synthSettings);
    }
}

export default Song;
