import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class Synth extends Component {
  state = {
    oscillator: {
      type: triangle
    },
    envelope: {
      attack: 0.005,
      decay: 0.1,
      sustain: 0.3,
      release: 1
    }
  };

  componentDidMount() {
    this.setState({ ...this.state, synth: this.props.synth });
  }

  updateEnvelope() {}

  render() {
    const oscillator = this.props.oscillator;
    const { attack, decay, sustain, release } = this.props.envelope;
    const envelopeControls = { attack, decay, sustain, release };
    return (
      <div className="envelope-controls">
        {Object.entries(envelopeControls).map((key, value) => {
          return (
            <CircularInput
              key={key}
              value={value}
              onChange={(key, value) => this.updateEnvelope(key, value)}
            >
              <CircularTrack />
              <CircularProgress />
              <CircularThumb />
            </CircularInput>
          );
        })}
      </div>
    );
  }
}
