import Tone from "tone";

class Song {
  constructor() {
    this.tempo = 120;
    this.instruments = [];
    this.sequences = [];
  }

  addSynth = (type = 'triangle') => {
    this.instruments.push(new Tone.Synth(type).toMaster());
    return this.getSynths();
  };

  getSynths = () => {
    return this.instruments;
  };

  getSynth = idx => {
    return this.instruments[idx];
  };

  updateSynth = (idx, props) => {
    //Envelope update
    this.instruments[idx].envelope.attack = props.envelope.attack;
    this.instruments[idx].envelope.decay = props.envelope.decay;
    this.instruments[idx].envelope.sustain = props.envelope.sustain;
    this.instruments[idx].envelope.release = props.envelope.release;
  };

  playSynth = (idx, notes = []) => {
    const synthPart = new Tone.Sequence(
      (time, note) => {
        this.instruments[idx].triggerAttackRelease(note, '8n', time);
      },
      notes,
      "8n"
    );
    console.log('synth active');
    synthPart.start();
  };

  play = () => {
    Tone.Transport.start();
  };

  stop = () => {
      console.log('stop');
    Tone.Transport.stop();
  };
}

export default Song;
