import React from "react";
import "./App.css";
import ControlsBar from "./ControlsBar";
import Song from "./Song.js";
import Synth from "./Synth";

export default class App extends React.Component {
  state = {
    song: new Song()
  };

  addNewSynth() {
    const newSong = { ...this.state.song };
    newSong.instruments = this.state.song.addSynth();
    this.setState({
      ...this.state,
      song: newSong
    });
  }

  updateSynth(idx, props) {
    const newSong = { ...this.state.song };
    newSong.instruments = this.state.song.updateSynth(idx, props);
    this.setState({
      ...this.state,
      song: newSong
    });
  }

  playSynth(idx, notes) {
    this.state.song.playSynth(idx, notes);
  }

  render() {
    const synths = this.state.song.getSynths();
    return (
      <div className="App">
        <button onClick={() => this.state.song.addSynth()}>Add Synth</button>
        <div className="synth-rack">
          {synths.map((synth, idx) => (
            <Synth key={idx} idx={idx} {...synth} updateSynth={(idx, props) => this.updateSynth(idx, props)} playSynth={(idx, notes) => this.playSynth(idx, notes)}/>
          ))}
        </div>
      </div>
    );
  }
}
