import React, { Component } from "react";
import 'PianoRollComponent.css';

const notes = ["B", "A#", "A", "G#", "G", "F#", "F", "E", "D#", "D", "C#", "C"];

export default class PianoRollComponent extends Component {
    componentDidMount() {
    const initialScale = 1, toScale = 6, rows = 12, cols = 16;
    for (let scale = toScale; scale > initialScale - 1; scale--) {
      for (let i = 0; i < rows; i++) {
        let step = true;
        let row = document.createElement("tr");
        let noteRow = document.createElement("th");
        noteRow.className = "note-cell";
        noteRow.innerHTML = notes[i] + scale;
        row.appendChild(noteRow);

        for (let j = 0; j < cols; j++) {
          let col = document.createElement("td");
          if (j % 4 === 0) step = !step;
          if (step) col.className = "cell-bar";
          col.setAttribute("note", notes[i]);
          col.setAttribute("scale", scale);
          col.setAttribute("step", j);
          col.addEventListener("click", this.activateCell);
          row.appendChild(col);
        }
        document.querySelector('#sequencer').appendChild(row);
      }
    }
  }

  activateCell(e) {
    const element = e.target;
    element.classList.toggle('cell-active');
    this.reReadSequence();
  };

  reReadSequence() {
    const cells = document.querySelectorAll(".cell-active");
    let sequence = new Array(16).fill("");

    cells.forEach(cell => {
      let step = cell.getAttribute("step");
      let note = cell.getAttribute("note");
      let scale = cell.getAttribute("scale");
      if(typeof sequence[step] === 'string' && sequence[step].length > 0) {
        sequence[step] = [sequence[step], note + scale];
      } else if (Array.isArray(sequence[step])) {
        sequence[step].push(note + scale);
      } else {
        sequence[step] = note + scale;
      }
    });
    console.log(sequence)
    return sequence;
  };

  render() {
    return <div id="sequencer"></div>;
  }
}
