import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class Synth extends Component {
  state = {
    oscillator: {
      type: 'triangle'
    },
    envelope: {
      attack: 0.005,
      decay: 0.1,
      sustain: 0.3,
      release: 1
    }
  };

  componentDidMount() {
      this.setState({
          ...this.state,
          oscillator: this.props.oscillator,
          envelope: this.props.envelope
      })
  }

  updateEnvelope(envKey, envValue) {
      const newEnvelope = {...this.state.envelope};
      newEnvelope[envKey] = envValue;
      this.setState({
            ...this.state,
            envelope: newEnvelope
      });
  }

  render() {
    //Envelope controllers
    const {attack, decay, sustain, release} = this.state.envelope;
    const envelopeControls = {attack, decay, sustain, release};

    return (
      <div className="envelope-controls">
        {Object.values(envelopeControls).map((idx, value) => {
            console.log(idx, value)
          return (
            <CircularInput
              key={idx}
              value={value}
              onChange={(key, value) => this.updateEnvelope(key, value)}
            >
              <CircularTrack />
              <CircularProgress />
              <CircularThumb />
            </CircularInput>
          );
        })}
      </div>
    );
  }
}
