import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class Synth extends Component {
  state = {
    oscillator: {
      type: 'triangle'
    },
    envelope: {
      attack: 0.005,
      decay: 0.1,
      sustain: 0.3,
      release: 1
    }
  };

  componentDidMount() {
      this.setState({
          ...this.state,
          oscillator: this.props.oscillator,
          envelope: this.props.envelope
      })
  }

  updateEnvelope(envKeyValue) {
      const newEnvelope = {...this.state.envelope};
      newEnvelope[envKeyValue[0]] = envKeyValue[1];
      this.setState({
            ...this.state,
            envelope: newEnvelope
      });
      return envKeyValue[1];
  }

  render() {
    //Envelope controllers
    const {attack, decay, sustain, release} = this.state.envelope;
    const envelopeControls = {attack, decay, sustain, release};
    return (
      <div className="envelope-controls">
        {Object.entries(envelopeControls).map((value) => {
            console.log(value);
          return (
            <CircularInput
              key={value[0]}
              value={value[1]}
              onChange={(value) => this.updateEnvelope(value)}
            >
              <CircularTrack />
              <CircularProgress />
              <CircularThumb />
            </CircularInput>
          );
        })}
      </div>
    );
  }
}
