import React, { Component } from "react";
import {
  CircularInput,
  CircularTrack,
  CircularProgress,
  CircularThumb
} from "react-circular-input";

export default class Synth extends Component {
  state = {
    synth: this.props.synth
  };

  componentDidMount() {
    this.setState({ ...this.state, synth: this.props.synth });
  }

  updateEnvelope() {}

  render() {
    const oscillator = this.props.oscillator;
    const { attack, decay, sustain, release } = this.props.envelope;
    const envelopeControls = { attack, decay, sustain, release };
    return (
      <div className="envelope-controls">
        {Object.entries(envelopeControls).map((key, envValue) => {
          const [value, setValue] = React.useState(envValue);
          return (
            <CircularInput
              key={key}
              value={value}
              onChange={setValue}
            >
              <CircularTrack />
              <CircularProgress />
              <CircularThumb />
            </CircularInput>
          );
        })}
      </div>
    );
  }
}
